TRUNCATE TABLE `#@_onepage`;
INSERT INTO `#@_onepage` (`oneorder`, `onetitle`, `onepath`, `onename`, `onekeyword`, `onedescription`, `onecontent`, `onetemplate1`, `onetemplate2`, `ishome`) VALUES (1, '提供最专业的网站建设服务', 'index.html', '首页', '建站服务,建站咨询,建站策划,网站制作,网页制作,网站建设,网站策划', '上海建站服务网是专业的网站建设服务公司，专注于企业网站制作，并独立开发了专门用来建设网站的内容管理系统（LazyCMS），在众多的网站建设公司中成为了最专业的网站建设公司。', '<p>上海建站服务网是专业的网站建设服务公司，专注于企业网站制作，并独立开发了专门用来建设网站的内容管理系统（LazyCMS），在众多的网站建设公司中成为了最专业的网站建设公司。</p>', 'template/home.html', 'template/inside/onepage/default.html', '1');
INSERT INTO `#@_onepage` (`oneorder`, `onetitle`, `onepath`, `onename`, `onekeyword`, `onedescription`, `onecontent`, `onetemplate1`, `onetemplate2`, `ishome`) VALUES (2, '关于我们', 'about.html', '关于我们', '关于我们', '上海建站服务网是专业的网站建设服务公司，专注于企业网站制作，并独立开发了专门用来建设网站的内容管理系统（LazyCMS），在众多的网站建设公司中成为了最专业的网站建设公司。', '<p>上海建站服务网是专业的网站建设服务公司，专注于企业网站制作，并独立开发了专门用来建设网站的内容管理系统（LazyCMS），在众多的网站建设公司中成为了最专业的网站建设公司。</p>', 'template/default.html', 'template/inside/onepage/default.html', '0');
INSERT INTO `#@_onepage` (`oneorder`, `onetitle`, `onepath`, `onename`, `onekeyword`, `onedescription`, `onecontent`, `onetemplate1`, `onetemplate2`, `ishome`) VALUES (3, '联系方式', 'contact.html', '联系方式', '联系方式', '首先声明，本站是属于工作室性质的个人组织站点，目前还没注册公司，但是我们有实力，提供一切企业建站服务。 我们独立开发了专门用来建设网站的内容管理系统（LazyCMS），我想此系统(LazyCMS.net)可以证明我们的实力。 如果你需要我们提供任何服务，在白天尽量使用网上交流，18:00-23:00 可以使用电话交流。 QQ:81595945 Email:mylukin(at)gmail.com ...', '<p>首先声明，本站是属于工作室性质的个人组织站点，目前还没注册公司，但是我们有实力，提供一切企业建站服务。</p>\r\n<p>我们独立开发了专门用来建设网站的内容管理系统（<a target="_blank" href="http://www.lazycms.net/">LazyCMS</a>），我想此系统(LazyCMS.net)可以证明我们的实力。</p>\r\n<p>如果你需要我们提供任何服务，在白天尽量使用网上交流，<font color="#ff0000">18:00-23:00 可以使用电话交流。</font></p>\r\n<p>QQ:81595945</p>\r\n<p>Email:mylukin(at)gmail.com <span style="color: #ff0000">(记得更换中间那个@符号)</span></p>\r\n<p>手机：13641738806 <span style="color: #ff0000">(接电话时间：18:00-23:00，其它时间勿扰。推广销售类请勿扰，我们不需要此服务。)</span></p>', 'template/default.html', 'template/inside/onepage/default.html', '0');
INSERT INTO `#@_onepage` (`oneorder`, `onetitle`, `onepath`, `onename`, `onekeyword`, `onedescription`, `onecontent`, `onetemplate1`, `onetemplate2`, `ishome`) VALUES (4, '付款方式', 'payment.html', '付款方式', '付款方式', '中国招商银行： 开户人：卢现升 开户行：上海市浦东新区东方路支行 帐&nbsp;&nbsp;&nbsp;号：6225 8802 1598 6895 中国农业银行： 开户人：卢现升 开户行：上海市浦东新区浦三路支行 帐&nbsp;&nbsp; 号：95599 8003 07370 66719 中国银行： 开户人：卢现升 开户行：上海市浦东新区临沂路支行 帐&nbsp;&nbsp; 号：6013 82...', '<p><strong>中国招商银行：</strong><br />\r\n开户人：卢现升<br />\r\n开户行：上海市浦东新区东方路支行<br />\r\n帐&nbsp;&nbsp;&nbsp;号：6225 8802 1598 6895</p>\r\n<p><strong>中国农业银行：<br />\r\n</strong>开户人：卢现升<br />\r\n开户行：上海市浦东新区浦三路支行<br />\r\n帐&nbsp;&nbsp; 号：95599 8003 07370 66719</p>\r\n<p><strong>中国银行：<br />\r\n</strong>开户人：卢现升<br />\r\n开户行：上海市浦东新区临沂路支行<br />\r\n帐&nbsp;&nbsp; 号：6013 8208 0006 9367 074</p>', 'template/default.html', 'template/inside/onepage/default.html', '0');

