INSERT INTO `#@_archives_sort` (`sortid`, `sortid1`, `modelid`, `sortorder`, `sortname`, `sortpath`, `keywords`, `description`, `sortopen`, `sorttemplate1`, `sorttemplate2`, `pagetemplate1`, `pagetemplate2`) VALUES (6, 2, 1, 6, '网站推广', 'Knowledge/SEO', '', '', 0, 'template/default.html', 'template/inside/article[list]/default.html', 'template/default.html', 'template/inside/article[page]/default.html');
INSERT INTO `#@_archives_sort` (`sortid`, `sortid1`, `modelid`, `sortorder`, `sortname`, `sortpath`, `keywords`, `description`, `sortopen`, `sorttemplate1`, `sorttemplate2`, `pagetemplate1`, `pagetemplate2`) VALUES (7, 2, 1, 7, '常见问题', 'Knowledge/FAQ', '', '', 0, 'template/default.html', 'template/inside/article[list]/default.html', 'template/default.html', 'template/inside/article[page]/default.html');
